mapper.update:
UPDATE board
SET title = #{title}, text=#{text}, regDate=now()
WHERE id=#{id}

mapper.select:
SELECT id, userId, title, writer, viewCnt, date_format(regDate, '%y-%m-%d %H:%i:%s') as regDate, text
FROM board
WHERE id=#{id}

mapper.update:
UPDATE board
SET viewCnt = viewCnt+1
WHERE id = #{id}

mapper.select:
SELECT id, userId, title, writer, viewCnt, date_format(regDate, '%y-%m-%d %H:%i:%s') as regDate, text FROM board
WHERE ${key} LIKE concat('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
